public class ShortestPalindrome {
    public String shortestPalindrome(String s) {
        if(s==null||s.length()==0) return "";
        if(s.length()==1) return s;

        int maxLen=0;
        for(int j=s.length();j>=1;j--){
            String sub = s.substring(0,j);
            if(isPalindrome(sub)){
                maxLen=j;
                break;
            }
        }

        String tail = s.substring(maxLen,s.length());
        StringBuilder sb = new StringBuilder(tail);
        String mirror = sb.reverse().toString();
        return mirror+s;
    }

    private boolean isPalindrome(String s){
        if(s==null||s.length()<=1) return true;
        int l=0;
        int r=s.length()-1;
        while(l<r){
            if(s.charAt(l)!=s.charAt(r))
                return false;
            l++;
            r--;
        }
        return true;
    }

    public static void main(String[] args) {
        String s = "";
        ShortestPalindrome inst = new ShortestPalindrome();
        System.out.println(inst.shortestPalindrome(s));
    }
}
